export class ScrollService {
  constructor() {
    this.toReset = false;
  }

  init(node) {
    this.node = node;
    this.previousScrollHeightMinusTop = 0;
    this.readyFor = 'up';
  }

  restore() {
    if (this.toReset) {
      console.log("restore");

      if (this.readyFor === 'up') {
        this.node.scrollTop = this.node.scrollTopMax - this.previousScrollHeightMinusTop;
      }

      this.toReset = false;
    }
  }

  down() {
    if (this.node) {
      console.log(26);
      this.node.scrollTop = this.node.scrollTopMax;
    }
  }

  prepareFor(direction) {
    this.toReset = true;
    this.readyFor = direction || 'up';
    this.previousScrollHeightMinusTop = this.node ? this.node.scrollTopMax - this.node.scrollTop : 0;
  }

}
